import React from "react";
import ReactDOM from "react-dom";
import axios from "axios";
class LoginComponent extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      emailid: null,
      emps: [],
      password: null
    };
  }

  handleChange = e => {
    this.setState({
      [e.target.name]: e.target.value
      //  value : e.target.value
    });
    console.log(this.state.password);
  };

  handleSubmit = event => {
    event.preventDefault();

    axios.get(`https://reactcrudapp-17704.firebaseio.com`).then(res => {
      console.log(res);
      alert(res.data);
      console.log(res.data);
    });
  };
  componentDidCatch() {}
  render() {
    let firebaseConfig = {
      apiKey: "AIzaSyDm9baRK9E_oYLzo6BAWRhzhMr7jUGW09M",
      authDomain: "reactcrudapp-17704.firebaseapp.com",
      databaseURL: "https://reactcrudapp-17704.firebaseio.com",
      projectId: "reactcrudapp-17704",
      storageBucket: "",
      messagingSenderId: "501130593797",
      appId: "1:501130593797:web:10814c59bdf7ef700f3e6c"
    };
    // Initialize Firebase

    return (
      <div>
        <h3> Login </h3>

        <div class="container">
          <div class="jumbotron">
            <div class="form-group">
              <label for="email">Email address:</label>
              <input
                type="email"
                class="form-control"
                name="emailid"
                onChange={this.handleChange}
              />
            </div>
            <div class="form-group">
              <label for="pwd">Password:</label>
              <input
                type="password"
                class="form-control"
                name="password"
                onChange={this.handleChange}
              />
            </div>
            <div class="checkbox">
              <label>
                <input type="checkbox" /> Remember me
              </label>
            </div>
            <button
              type="submit"
              class="btn btn-default"
              onClick={this.handleSubmit}
            >
              Submit
            </button>
          </div>
        </div>
      </div>
    );
  }
}

export default LoginComponent;
